
$(document).ready(function(){

    $(window).on('beforeunload', function(){
        $(window).scrollTop(0);
    });

//click scroll to nuestro trabajo

    $('#nuestroTrabajo').click(function(){
        $('html, body').animate({scrollTop: $('.nuestro-container').offset().top}, 600);
    });

//plugin to stop navbar fixed position
    
    var windw = this;

    $.fn.followTo = function ( pos ) {
        var $this = this,
            $window = $(windw);

        $window.scroll(function(e){
            if ($window.scrollTop() > pos) {
                $this.css({
                    position: 'absolute',
                    top: pos
                });
            } else {
                $this.css({
                    position: 'fixed',
                    top: 0
                });
            }
        });
    };

    $('.navbar-header').followTo(530);

//carousel

    $('.carousel').carousel({
        interval: 2400
    });

//click scroll to involucrarse

    $('#involucrarse').click(function(){
        $('html, body').animate({scrollTop: $('.involv').offset().top}, 600);
    });

    // $('#contact-btn').click(function(){
    //     $('[data-toggle="popover"]').popover();
    // });

    $('#contact-btn').popover();

//click show overlay contact form 
//note: add click function to hide overlay when submit contact form

    $('.contact').click(function(){
        $('.overlay').show(600);
    });

// click to scroll to donate form

    $('.donateContainer').hide();
    $('.donate').click(function(){
        $('.donateContainer').show();
        $('html, body').animate({scrollTop: $('.donateContainer').offset().top}, 600);
    });

    $('.donateContainer').hide();
    $('#donate2').click(function(){
        $('.donateContainer').show();
        $('html, body').animate({scrollTop: $('.donateContainer').offset().top}, 1000);
    });

//close donate form

    $('.close-icon').click(function(){
        $('.donateContainer').hide(600);
    });

});


